INSERT INTO `thesis` VALUES ('122', '1', '3', '远红外图像行人分割研究', '数字图像处理', '其他', '应用研究', '<p>有别于传统的可见光摄像头，远红外摄像头通过场景的温差成像，从而使得特别适合进行夜间的行人检测。远红行人分割是远红行人检测中至关重要的阶段。本题目拟研究远红行人分割方法。具体地，将利用场景先验知识，辅助实现远红行人的分割，从而提出一种改进的远红外行人分割方法。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-12 21:14:15', '2016-10-12 21:52:12');
INSERT INTO `thesis` VALUES ('123', '2', '1', '校园宿舍及网络报修系统开发与应用', '论文类', '其他', '毕业论文', '<p>校园宿舍及网络报修系统开发与应用</p>', '1', '16', null, null, '2017-05-01 00:00:00', '0', '1', '2016-10-12 21:16:33', '2017-08-01 11:07:18');
