INSERT INTO `thesis` VALUES ('131', '2', '1', '基于SSH+EasyUI公司账务管理系统', '设计类', '导师出题', '毕业论文', '<p>企业基本账务管理，包括收入、支出、资产、负债等基本功能记录，实现简单的利润分析和成本核算功能。</p>', '1', '20', '107', null, '2016-10-12 00:00:00', '0', '1', '2016-10-12 21:37:11', '2016-10-12 21:37:11');
INSERT INTO `thesis` VALUES ('132', '1', '3', '远红行人分类方法研究', '人工智能', '其他', '应用研究', '<p>在进行行人检测时，分类器设计，是影响行人分类至关重要的步骤。该题目拟分析基于目前流行的远红外行人描述特征，不同的单分类（支持向量机、Adaboost、人工神经网络等）的分类效果。从而通过分类准确率、分类效率指标，评估出在特定的训练样本和特征的前提下，最优的分类器。进一步，拟研究分类器的最佳结合方式，从而提出效果更佳的远红外行人分类器。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-12 21:38:26', '2016-10-12 21:38:26');
