INSERT INTO `thesis` VALUES ('137', '1', '3', '远红行人跟踪方法研究', '人工智能', '其他', '应用研究', '<p>行人跟踪是行人行为预测的重要基础。目前，远红外图像日益流行，所以研究鲁棒的远红外行人跟踪方法具有重要意义。无论在车载辅助驾驶、视频监控、智能机器人领域，均具有广泛的应用。改课题，拟针对远红外行人的特性，基于卡尔曼滤波或粒子滤波跟踪方法，提出改进的远红外行人跟踪方法，并通过实验验证方法的可行性。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-12 21:51:19', '2016-10-12 21:51:19');
INSERT INTO `thesis` VALUES ('138', '2', '1', '基于微信公众号个人助理服务平台', '设计类', '学生定题', '毕业论文', '<p>基于微信公众号，实现个人诸如商品价格监控等多项活助理功能，如价格监控等。</p>', '1', '20', '128', null, '2016-10-12 00:00:00', '0', '1', '2016-10-12 21:53:37', '2016-10-12 21:53:37');
