INSERT INTO `thesis` VALUES ('601', '1', '3', '基于手机的华农公益众筹平台', '应用软件开发', '科研项目', '基础研究', '<p>针对华南农业大学的公益捐助，开发一个基于手机的教育公益众筹平台。</p>\r\n\r\n<p>TEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:46:18', '2016-10-18 14:46:18');
INSERT INTO `thesis` VALUES ('602', '1', '3', '大学生综合测评系统的设计与实现', '应用软件开发', '科研项目', '基础研究', '<p>高校综合测评工作多采用手工录入、人工提交信息的方式，占用了综合测评相关老师和学生的大量时间和精力。针对这种现状，研究高校综合测评工作的业务流程，设计和实现一个基于Web的大学生综合测评系统。</p>\r\n\r\n<p>TEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', '659', null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:46:51', '2016-10-18 14:46:51');
INSERT INTO `thesis` VALUES ('603', '1', '3', '基于手机的小学生课程日历系统', '应用软件开发', '科研项目', '基础研究', '<p>小学生由于年龄小，认知程度有限，经常会遗忘第二天所要上的课程信息，或者忘记课程所要准备的备品信息；另外家长为了孩子的全面发展，会给学生选择兴趣班学习，这些信息也需要进行全面周详的管理。开发一个适用于小学生的课程日历系统，能够帮助家长合理安排时间。</p>\r\n\r\n<p><br />\r\nTEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', '656', null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:47:19', '2016-10-18 14:47:19');
INSERT INTO `thesis` VALUES ('604', '1', '3', '基于手机的在线教辅系统', '应用软件开发', '科研项目', '基础研究', '<p>设计实现一个在线教辅平台，是课堂教学的有益补充，体现个性化学习，可以显著地提高老师和学校的工作效率和学生的学习质量。</p>\r\n\r\n<p><br />\r\nTEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:47:50', '2016-10-18 14:47:50');
INSERT INTO `thesis` VALUES ('605', '1', '3', '基于LBS应用的逛街宝系统', '应用软件开发', '科研项目', '基础研究', '<p>结合LBS技术，对用户实现定位、查看周边商店信息，检索购物信息以及评价信息等。</p>\r\n\r\n<p><br />\r\nTEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', '587', null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:48:57', '2016-10-18 14:48:57');
INSERT INTO `thesis` VALUES ('606', '1', '3', '党校培训考试管理系统', '应用软件开发', '科研项目', '基础研究', '<p>目前在高校中大部分党校结业的考试还是采用过去传统的考试方式,这种方式既耗费人力物力又浪费时间。利用计算机相关技术设计开发一个党校培训考试系统，为党校培训工作提供更有力的支撑。</p>\r\n\r\n<p><br />\r\nTEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com<br />\r\n&nbsp;</p>', '1', '228', '473', null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:49:26', '2016-10-18 14:49:26');
INSERT INTO `thesis` VALUES ('607', '1', '3', 'DNA三维结构模建', '应用软件开发', '科研项目', '基础研究', '<p>根据用户给定的DNA序列，模建DNA的标准B型三维结构.<br />\r\nTEL:13527723968&nbsp;&nbsp;&nbsp; email: 632499155@qq.com</p>', '1', '228', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-18 14:51:01', '2016-10-18 14:51:01');
INSERT INTO `thesis` VALUES ('608', '1', '3', 'SQL漏洞检测系统的研究与实现', '网络技术与应用', '其他', '应用研究', '<p><span style=\"background-color:#ffffff; color:#333333\">SQL漏洞就是Web安全中最常见的的一种安全漏洞。随着未来大数据的崛起,Web应用会越来越倾向于以数据为中心,而SQL漏洞问题自然是Web安全研究的重点。所以研究SQL漏洞的检测和防御对Web安全具有重要意义。</span></p>\r\n\r\n<p><span style=\"background-color:#ffffff; color:#333333\">要求：</span></p>\r\n\r\n<p><span style=\"background-color:#ffffff; color:#333333\">1、熟悉SQL漏洞的攻击原理</span></p>\r\n\r\n<p><span style=\"background-color:#ffffff; color:#333333\">2、了解SQL漏洞检测的相关技术，</span></p>\r\n\r\n<p><span style=\"background-color:#ffffff; color:#333333\">3、</span>分析对比<span style=\"background-color:#ffffff; color:#333333\">经典方法和国内外的先进技术的优缺点</span></p>\r\n\r\n<p><span style=\"background-color:#ffffff; color:#333333\">4、设计一种SQL漏洞的检测</span></p>', '1', '218', '648', null, '2017-04-23 00:00:00', '0', '1', '2016-10-18 14:52:22', '2016-10-18 14:52:22');
