INSERT INTO `thesis` VALUES ('634', '1', '3', '基于Web的无线传感器网络可视化管理软件开发', '应用软件开发', '科研项目', '应用研究', '<p>为无线传感器网络开发一个可视化管理软件，管理采集的数据，具有可视化功能，包括网络在地图上的可视化、节点拓扑结构可视化、节点采集信息可视化、网络流量可视化等。</p>', '1', '255', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-18 16:39:15', '2016-10-18 16:40:36');
