INSERT INTO `thesis` VALUES ('775', '1', '3', '宠物健康管家APP设计与开发', '应用软件开发', '科研项目', '应用研究', '<p>现在越来越多人选择在家里养宠物，但是由于工作忙，照料宠物的时间很少。随着移动互联网和传感器的兴起，在宠物身上佩戴具有定位功能、身体指标监控的传感器，人可以在手机端随时查看宠物的位置、宠物的健康状态，以实现对宠物的健康管理。</p>', '1', '864', null, null, '2017-03-10 00:00:00', '0', '1', '2016-10-18 21:46:50', '2016-10-18 21:46:50');
