INSERT INTO `thesis` VALUES ('824', '1', '3', '定时提醒桌面小工具的设计与开发', '应用软件开发', '生产实践', '应用研究', '<p>目的：定时提醒工作人员休息，以防过度用眼</p>\r\n\r\n<p>功能：1. 设置提醒方式、时间</p>\r\n\r\n<p>&nbsp; &nbsp; 2. 定时弹出桌面工具，提示工作人员休息</p>', '1', '270', '571', null, '2017-05-01 00:00:00', '0', '1', '2016-10-18 22:55:03', '2016-10-18 22:55:03');
