INSERT INTO `thesis` VALUES ('826', '1', '3', '酒店在线点菜系统的设计与开发', '应用软件开发', '生产实践', '应用研究', '<p>目的：酒店菜谱无纸化，实现在线点餐，节约等待时间</p>\r\n\r\n<p>功能：1. 管理员录入菜品（名称、介绍、价格）</p>\r\n\r\n<p>    2. 顾客登录系统，自助点餐</p>', '1', '270', '583', null, '2017-05-01 00:00:00', '0', '1', '2016-10-18 23:02:14', '2016-10-18 23:03:56');
INSERT INTO `thesis` VALUES ('827', '1', '3', '基于Java 的茶叶溯源系统', '应用软件开发', '科研项目', '基础研究', '', '1', '223', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-18 23:02:34', '2016-10-18 23:02:34');
INSERT INTO `thesis` VALUES ('828', '1', '3', '基于大数据的数据检索引擎系统', '大数据', '生产实践', '应用研究', '<p>对当前数据量大时响应时间慢、没办法实时查询的情况进行解决，基于大数据的数据检索引擎系统</p>', '1', '241', '333', null, '2017-04-30 00:00:00', '0', '1', '2016-10-18 23:13:47', '2016-10-18 23:13:47');
INSERT INTO `thesis` VALUES ('829', '1', '3', '畜禽产品质量安全追溯可信第三方平台设计开发', '应用软件开发', '科研项目', '应用研究', '<p>调研畜牧业基本养殖条件，对动物产品的养殖流程及关键环节进行整理提炼，可追溯的可信第三方信息管理平台，能够满足畜禽产品养殖、物流仓储、销售等环节数据采集汇总需求，并支持生产者、销售者、监管部门和消费者不同角色的相应操作权限；使用PHP+SQL进行系统开发，能实现上述主要功能模块，要求完成系统设计开发并测试及试用。</p>\r\n\r\n<p>&nbsp;</p>', '1', '233', null, null, '2017-04-18 00:00:00', '0', '1', '2016-10-18 23:17:53', '2016-10-18 23:17:53');
INSERT INTO `thesis` VALUES ('830', '1', '3', '基于RFID的实验室设备管理APP系统', '应用软件开发', '生产实践', '设计性', '<p>学习RFID系统应用技术及功能，调研实验室资产设备管理的业务需求，根据需求整理管理流程和主要功能模块，与团队配合实现管理信息数据库设计，并采用Android开发技术实现实验室资产设备管理信息的APP系统开发，最后测试并试用。</p>', '1', '233', '618', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 11:23:09', '2016-10-19 11:23:09');
INSERT INTO `thesis` VALUES ('831', '2', '2', '基于Web的农业文本信息自动抽取方法', '设计类', '导师出题', '毕业论文', '<p>目前Web上的网页信息呈现模式主要是为了方便用户的浏览，然而却也导致应用程序无法直接解析和利用这些网页数据作进一步的分析。Web信息抽取主要是为了自动识别网页中的正文内容，从网络上半结构化的HTML页面中提取网页正文信息，去掉无关的冗余信息（如导航栏、广告推荐等），从而将解析到的文本信息用于数据的分析、查询统计及结构化展示等。</p>', '1', '18', '189', null, '2017-03-31 00:00:00', '0', '1', '2016-10-19 11:50:12', '2016-10-19 11:50:12');
INSERT INTO `thesis` VALUES ('832', '1', '3', '社区医院综合管理信息系统的设计', '应用软件开发', '生产实践', '应用研究', '<p>本系统根据E-R数据模型进行数据库表详细设计，基于Java EE平台，搭建SSH框架，使用Struts2框架把数据与逻辑分开，采用Hibernate框架对持久层进行支撑，再使用Spring框架对Struts2框架与Hibernate框架进行整合，降低系统各层间的耦合，使系统的维护与扩展更方便，编写代码实现社区医院综合管理。</p>', '1', '238', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 14:43:39', '2016-10-19 14:43:39');
INSERT INTO `thesis` VALUES ('833', '1', '3', '校园易物app开发', '应用软件开发', '科研项目', '应用研究', '<p>同学们可以在手机app上发布求购信息和出售信息，公布商品的文字描述、图片和推销视频。该易物app实用性较强。</p>', '1', '864', '561', null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 15:00:39', '2016-10-19 15:00:39');
INSERT INTO `thesis` VALUES ('834', '1', '3', '预算管理系统的设计与实现', '应用软件开发', '生产实践', '应用研究', '<p>根据包括报表管理，指标管理，预算模型，预算分析，流程管理，消息管理，系统管理等，功能间彼此独立，数据间逻辑关系密切。系统采用B/S模式，</p>', '1', '238', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:02:44', '2016-10-19 15:02:44');
INSERT INTO `thesis` VALUES ('835', '1', '3', '小型就业管理信息系统的设计与实现', '应用软件开发', '科研项目', '基础研究', '<p>小型就业系统主要功能有：学生基本信息和就业信息管理，简历管理，企业信息管理，招聘信息管理，招聘会管理，就业指导信息管理，事务通知管理，用户管理，院系管理，采用基于Java EE平台的MyEclipse开发工具与框架Struts2+ Hibernate3实现该</p>', '1', '238', '417', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:05:17', '2016-10-19 15:05:17');
INSERT INTO `thesis` VALUES ('836', '1', '3', '基于Unity3d的手机游戏设计', '应用软件开发', '生产实践', '基础研究', '<p>研究Unity3D技术并设计实现手机游戏，游戏内容描述：生存类游戏，玩家需在一个地图中躲避或者击杀向他袭击而来的怪物，当玩家受到攻击时玩家生命值会减少，玩家生命值为0时游戏结束。</p>', '1', '220', '534', null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:10:52', '2016-10-19 15:11:42');
INSERT INTO `thesis` VALUES ('837', '1', '3', '找不同手机益智游戏开发', '应用软件开发', '科研项目', '应用研究', '<p>开发一款手机游戏，游戏显示两张相似的图片，要求用户找出两张图片中几个不同的地方，全部找对，游戏晋级，否则，游戏重来。</p>', '1', '864', '483', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:13:09', '2016-10-19 15:13:09');
INSERT INTO `thesis` VALUES ('838', '1', '3', '钓鱼爱好者app开发', '应用软件开发', '科研项目', '应用研究', '<p>这是一款基于地图应用的app。钓鱼爱好者加入该app，可以分享新发现的钓鱼地点，将该钓鱼地点加入地图中并进行标识，同时推送给该钓鱼地点附近的爱好者；钓鱼爱好者还可以以图片的形式发布今天的钓鱼成果，并可标价出售一些鱼给其他爱好者。</p>', '1', '864', '429', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:20:55', '2016-10-19 15:21:55');
INSERT INTO `thesis` VALUES ('839', '1', '3', '高校学生档案信息管理系统', '应用软件开发', '其他', '设计性', '<p>使用任何类型编程语言，实现一款网络版或单机版的档案信息管理系统，可以对高校学生的档案进行电子化管理，具体包括档案信息录入，档案多条件查询，档案分析等。数据库可以使用Access 或 SQL Server等。</p>', '1', '862', null, null, '2017-04-13 00:00:00', '0', '1', '2016-10-19 15:23:08', '2016-10-19 15:23:08');
INSERT INTO `thesis` VALUES ('840', '1', '3', '基于JSP的 高校人机交互课程学生考勤管理系统', '应用软件开发', '生产实践', '基础研究', '<p>开发环境是Eclipse、MyEclipse的，为JSP/JAVA程序；</p>\r\n\r\n<p>实现自动点名，核查及平时成绩核算</p>', '1', '245', '552', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:26:07', '2016-10-19 15:26:07');
INSERT INTO `thesis` VALUES ('841', '1', '3', '图书信息管理系统设计与实现', '应用软件开发', '生产实践', '应用研究', '<p>【任务】<br />\r\n1. 实现图书管图书信息的浏览、增删查改等功能 </p>\r\n\r\n<p>【要求】<br />\r\n1. 使用Java或C#<br />\r\n2. 数据库根据自身情况进行选择</p>\r\n\r\n<p>【联系方式】<br />\r\n手机：13760732070<br />\r\nemail：wangct@scau.edu.cn</p>', '1', '265', '510', null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:26:26', '2016-10-19 15:29:43');
INSERT INTO `thesis` VALUES ('842', '1', '3', '图片标记系统的设计与实现', '应用软件开发', '其他', '设计性', '<p>设计一款图片标记软件，系统可以读入多种格式的图片，然后对图片的物体进行矩形框的标注，并将标注信息进行存储。矩形框进行标记时，由于不同问题的大小不一致，故要求系统能提供可变的矩形框，供用户进行调整。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:31:01', '2016-10-19 15:31:01');
INSERT INTO `thesis` VALUES ('843', '1', '3', '基于JSP的 人机交互教学网站的设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>双语教学的人机交互课程教学网站开发，包括课件制作，成绩统计，课后作业审查</p>\r\n\r\n<p>开发环境是Eclipse、MyEclipse的，为JSP/JAVA程序</p>', '1', '245', '553', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:32:33', '2016-10-19 15:32:33');
INSERT INTO `thesis` VALUES ('844', '1', '3', '多种题型在线考试系统设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>多题型在线考试系统，包括题库抽题，防止作弊成绩统计以及试卷分析</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:36:53', '2016-10-19 15:36:53');
INSERT INTO `thesis` VALUES ('845', '1', '3', '小型旅馆管理系统', '应用软件开发', '其他', '设计性', '<p>实现一款小型旅馆的管理系统，可以满足一般小型旅馆的日程管理需要。完成的功能：客房类型管理、客房（房间）管理、客房查询、客户查询、客户入住和客户结算。</p>\r\n\r\n<p>。可以实现对旅馆房间入住情况的可视化显示。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:37:37', '2016-10-19 15:37:37');
INSERT INTO `thesis` VALUES ('846', '1', '3', '基于深度学习的加密灰度图像压缩算法', '应用软件开发', '科研项目', '基础研究', '<p>【任务】<br />\r\n1. 利用深度学习实现加密灰度图像的压缩与重构&nbsp;</p>\r\n\r\n<p>【要求】<br />\r\n1. 使用Matlab、C或Python</p>\r\n\r\n<p>【联系方式】<br />\r\n手机：13760732070<br />\r\nemail：wangct@scau.edu.cn</p>', '1', '265', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:37:39', '2016-10-19 15:37:39');
INSERT INTO `thesis` VALUES ('847', '1', '3', '基于Qt的手机游戏开发', '应用软件开发', '科研项目', '基础研究', '<p>Qt, OpenCV</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:38:15', '2016-10-19 15:38:15');
INSERT INTO `thesis` VALUES ('848', '1', '3', '基于语义网的电子购物网站的设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>新型网络下的购物网站</p>', '1', '245', '570', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:39:57', '2016-10-19 15:39:57');
INSERT INTO `thesis` VALUES ('849', '1', '3', '跨平台网络游戏的开发与实现', '应用软件开发', '生产实践', '基础研究', '<p>Android, iphone, ipad跨平台游戏</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:42:08', '2016-10-19 15:42:08');
INSERT INTO `thesis` VALUES ('850', '1', '3', '员工工资管理系统', '应用软件开发', '其他', '其他', '<p>设计一个小型企业的工资管理系统，由计算机代替人工执行一系列诸如增加新员工，删除旧员工，工资查询，统计等操作。编程语言不限，数据库语言不限。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:43:36', '2016-10-19 16:00:15');
INSERT INTO `thesis` VALUES ('851', '1', '3', '基于Android 的个性化推荐商品网站的设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>个性化商品的推荐和对比</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:45:21', '2016-10-19 15:45:21');
INSERT INTO `thesis` VALUES ('852', '1', '3', '交互式网上自动答疑系统的设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>关键字匹配，友好的人机交互界面</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:48:52', '2016-10-19 15:48:52');
INSERT INTO `thesis` VALUES ('853', '1', '3', '公司门户网站建设', '应用软件开发', '其他', '其他', '<p>开发一个公司的门户网站，包括公司简介、公司产品的介绍、公司招聘信息管理、公司新闻管理等基本模块。编程语言和数据库不限。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 15:53:45', '2016-10-19 15:53:45');
INSERT INTO `thesis` VALUES ('854', '1', '3', '图书馆图书自动分类模型的设计与实现', '应用软件开发', '生产实践', '基础研究', '<p>书名，二维码或条形码，书摘中关键词寻找和匹配</p>', '1', '245', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:58:11', '2016-10-19 15:58:11');
INSERT INTO `thesis` VALUES ('855', '1', '3', '网上求职招聘系统', '应用软件开发', '其他', '设计性', '<p>开发一款网络版的网上求职系统，求职者可以进行注册，在线填写自己的简历，并按条件查询由招聘人员发布的招聘信息。招聘人员可以发布招聘信息，筛选符合一定条件的应聘人员，并进行简历查看。</p>', '1', '862', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 15:58:32', '2016-10-19 16:00:03');
INSERT INTO `thesis` VALUES ('856', '1', '3', '图书馆无人值班自动管理系统', '应用软件开发', '生产实践', '基础研究', '<p>图书馆管理系统</p>', '1', '245', '555', null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 16:01:10', '2016-10-19 16:01:10');
INSERT INTO `thesis` VALUES ('857', '1', '3', '房产销售网站', '应用软件开发', '其他', '设计性', '<p>开发一个专门销售房产的销售网站，需要卖房产的人可以注册，并上传图文房产信息，需要购买房子的人，可以查询特定条件下的房产信息。实现语言和数据库不限。</p>', '1', '862', null, null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 16:08:36', '2016-10-19 16:08:36');
INSERT INTO `thesis` VALUES ('859', '1', '3', '基于微信公众号的防作弊考勤系统', '应用软件开发', '生产实践', '应用研究', '<p>基于微信公众号的防作弊考勤系统</p>', '0', '275', null, null, '2017-05-20 00:00:00', '0', '1', '2016-10-19 19:19:07', '2016-10-19 19:19:07');
INSERT INTO `thesis` VALUES ('860', '1', '3', '在线培训管理系统的设计与实现', '应用软件开发', '生产实践', '设计性', '<p>本项目可以采用Tapestry、Spring、Hibernate组合框架，实现了在线培训管理系统。通过对教师、学员、培训管理者、培训决策者的相关需求分析，构建教学、学习、管理与决策的互动模式。从需求定义、培训安排、培训实施、培训评估与培训统计的全过程管理，实现了传统面授和网上培训相结合的混合式培训方式。<br />\r\n&nbsp;</p>', '0', '276', null, null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 21:13:17', '2016-10-19 21:13:17');
INSERT INTO `thesis` VALUES ('861', '1', '3', '基于微信的图书管理系统的设计与实现', '应用软件开发', '生产实践', '设计性', '<p>&nbsp;微信公众平台是一个开放的平台,同时微信公众平台开放了其API接口,任何机构都可以开发并构建基于开放接口的第三方服务平台,实现和机构应用的无缝对接。本项目旨在运用微信公众平台的API接口开发功能，设计和实现基于微信的图书馆服务系统，为用户提供快捷方便的图书馆相关信息查询服务。<br />\r\n&nbsp;</p>', '0', '276', null, null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 21:21:22', '2016-10-19 21:21:22');
INSERT INTO `thesis` VALUES ('862', '1', '3', '证件照的质量预检测系统的设计与实现', '应用软件开发', '生产实践', '设计性', '<p>近几年，随着电子政务的推进,网上证件照制作开始进入公众视野。一般证件照的制作流程是拍摄、裁剪、色彩优化和背景替换。如果用户拍摄的照片存在背景杂乱、映像模糊、有噪点或者曝光不均匀等问题时会对证件照制作带来麻烦或根本得不到合格的证件照,因此需要预先对用户拍摄的照片质量进行检测,如果质量太差应及时反馈给用户以便其重新拍摄。</p>', '0', '276', null, null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 21:39:00', '2016-10-19 21:39:00');
INSERT INTO `thesis` VALUES ('863', '1', '3', '基于Android 的智能考勤系统的设计与实现', '应用软件开发', '生产实践', '设计性', '<p>目前对学生进行考勤管理主要是以纸张记录考勤情况的方式，它既不能避免学生代替点名答到现象的发生，又浪费了大量的课堂教学时间。随着信息技术的高速发展，智能手机早已成为学生的必备品之一。根据手机地址的唯一性，设计了一套基于Android的智能考勤系统，通过智能手机来进行学生的课堂考勤管理，对于加强学生考勤管理，提高学生到课率和教学质量有着重要意义。<br />\r\n&nbsp;</p>', '0', '276', null, null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 21:52:37', '2016-10-19 21:52:37');
INSERT INTO `thesis` VALUES ('864', '1', '3', '自行车自助借还系统的设计与实现', '应用软件开发', '生产实践', '设计性', '<p>近几年，很多一线城市纷纷推出了绿色出行计划，用绿色环保低碳的出行方式取代汽车。其中，比较有成效的当属公共自行车。大量的自行车借还点，极大方便了市民的出行，同时起到了节能减排的作用。然而，自行车的借还不便，是目前正面临的另外一个需要解决的问题。本项目旨在提出一种新型的智能公共自行车借还系统，优化传统借/还车模式。例如：还车一方可直接把车&ldquo;还给&rdquo;需借车一方，需借车一方可以直接从需还车一方&ldquo;借取&rdquo;车辆，借/还车无需通过车站；当车站车满时，还车者有&ldquo;桩&rdquo;可还；当车站无车时，借车者有车可借，实现了借/还需求互补，也进一步简化了自行车的借还程序。</p>', '0', '276', null, null, '2017-04-20 00:00:00', '0', '1', '2016-10-19 22:23:57', '2016-10-19 22:23:57');
INSERT INTO `thesis` VALUES ('865', '1', '3', '奖贷助学金管理系统设计与实现', '应用软件开发', '生产实践', '应用研究', '<p>【任务】<br />\r\n1.实现奖学金、助学金、贷款等方面的管理</p>\r\n\r\n<p>2.实现奖学金电子化评定&nbsp;</p>\r\n\r\n<p>【要求】<br />\r\n1. 使用Java或C#<br />\r\n2. 数据库根据自身情况进行选择</p>\r\n\r\n<p>【联系方式】<br />\r\n手机：13760732070<br />\r\nemail：wangct@scau.edu.cn</p>', '0', '265', '666', null, '2017-04-23 00:00:00', '0', '1', '2016-10-19 22:26:09', '2016-10-19 22:26:09');
